\a&quot;);		break;	&#125;	switch (month) &#123;		case 1:case 3:case 5:case 7:case 8:case 10:case 12:date = 31; break;		case 4:case 6:case 9:case 11:date = 30; break;		case 2: 			if (year % 4 == 0 &amp;&amp; year % 100 != 0 || year % 400 == 0) &#123;				date = 29;			&#125;			else &#123;				date = 28;			&#125;break;		default:printf(&quot;月份输入有误!&quot;); 			exit(1);			break;	&#125;	printf(&quot;%d年%d月有%d天
