005 求100以内的素数#include &lt;stdio.h&gt;#include &lt;math.h&gt;	int main()&#123;	int i;  //判断素数	int j;  //循环次数	int m;  //所求范围数	int b = 0;  //合数个数	printf(&quot;请输入判断数：&quot;);	scanf_s(&quot;%d&quot;, &amp;i);	//思路一：判断一个整数 i 是否是素数，只需把 i 被 2 ~ i - 1 之间的每一个整数去除，如果都不能被整除，那么 i 就是一个素数。	for (j = 2; j &lt; i; j++) &#123;		if (i % j == 0) &#123;			b++;		&#125;	&#125;	if (b == 0) &#123;		printf(&quot;%d是素数&quot;, i);	&#125;	else &#123;		printf(&quot;%d不是素数&quot;, i);	&#125;	printf(&quot;
&quot;);	/*思路二：判断方法还可以简化。i 不必被 2 ~ i - 1 之间的每一个整数去除，只需被 2 ~ √i 之间的每一个整数去除就可以了。	如果 i 不能被 2 ~ √i 之间任一整数整除，i 必定是素数。*/		/*原因：因为如果 i 能被 2 ~ i - 1 之间任一整数整除，其两个因子必定有一个小于或等于 √i，另一个大于或等于 √i。	例如 16 能被 2、4、8 整除，16 = 2 * 8，2 小于 4，8 大于 4，16 = 4 * 4，4 = √16，因此只需判定在 2 ~ 4 之间有无因子即可。*/	printf(&quot;请输入素数范围（不包括输入值）：&quot;);	scanf_s(&quot;%d&quot;, &amp;m);	for (i = 2; i &lt; m; i++) &#123;		for (j = 2; j &lt;= (i / j); j++)			if (!(i % j)) break;		if (j &gt; (i / j))			printf(&quot;%d,&quot;, i);	&#125;	printf(&quot;
&quot;);	for (i = 2; i &lt; m; i++) &#123;		for (j = 2; j &lt; sqrt(i); j++) &#123;  //sqrt() 函数求平方根，其参数为 double 类型			if (i % j == 0) &#123;  // 如果完成所有循环，那么 i 为素数				break;			&#125;		&#125;		// 注意最后一次循环，会执行i++		if (j &gt; sqrt(i)) &#123;    		printf(&quot;%d,&quot;, i);		&#125;	&#125;	return 0;&#125;
