int WorkerManager::IsExist(int id) &#123;	int index = -1;	for (int i = 0; i &lt; this-&gt;m_EmpNum; i++) &#123;		if (this-&gt;m_EmpArray[i]-&gt;m_Id == id) &#123;			index = i;			break;		&#125;	&#125;	return index;&#125;
